clear && read -p "Table name?
" TABLENAME && echo -e $TABLENAME":" > ~/postgres_describe_table.tmp && echo "select c, data_type from public.columns where st = '$TABLENAME';" > table_name.tmp && psql -h production-redshift-primary.venmo.biz -U root -d dev -p 5439 -X -t -f table_name.tmp >> ~/postgres_describe_table.tmp && clear && less ~/postgres_describe_table.tmp ; rm ~/postgres_describe_table.tmp ; rm ~/table_name.tmp
